mapper.update:
UPDATE T_USER_INFO
SET
ISLOGIN = #{isLogin,jdbcType=INTEGER}
WHERE
USER_ID = #{userId,jdbcType=VARCHAR}

mapper.select:
SELECT * FROM T_PRODUCT_TYPE_LOCAL
WHERE L_C_PID = #{id,jdbcType=VARCHAR}
order by l_c_name

SELECT * FROM T_ORDER_SHIPMENT
WHERE 1=1

    AND ORDER_ID = #{orderId,jdbcType=VARCHAR}
