mapper.select:
SELECT t1.* FROM T_ADDRESS t1
WHERE t1.AMAZONORDERID IN (SELECT AMAZONORDERID FROM T_ORDER WHERE MERGEDID = #{amazonOrderId,jdbcType=VARCHAR})
WHERE t1.AMAZONORDERID = #{amazonOrderId,jdbcType=VARCHAR}

SELECT * FROM (select t1.id, t1.amazonorderid, t1.MERGEDID, t1.salesman, t1.salescompany, t1.salessource, t1.buyercountry, t1.buyeremail, t1.marketplaceId, t6.user_name, t5.shop_name, t5.merchant_id, t5.create_user, t1.runningtime, t1.intltracknum, t1.purchaseDate, t1.FULFILLMENTCHANNEL, t7.name transportCompanyName, t1.transportCompany, t1.shippingPrice, t1.localstatus, t1.remark, t1.update_time, t3.name countryName, t2.smallImage, t4.company_name, t1.paymentmethod, t2.sku, t2.asin, t2.tracknum, trunc(sum(t2.itempricermb + t2.shippingPricermb + t2.giftWrapPricermb + t2.commissionRMB),2) itemprice, trunc(sum(t2.cost),2) cost, trunc(sum(t2.refundment),2) refundment, trunc(sum(t2.itempricermb - t2.cost - t2.refundment - t1.shippingPrice + t2.shippingPricermb + t2.giftWrapPricermb + t2.commissionRMB),2) profit from T_ORDER t1 left join (select amazonorderid, wm_concat(smallimage) smallimage, wm_concat(sellersku) sku, wm_concat(asin) asin, wm_concat(tracknum) tracknum, trunc(sum(itempricermb),2) itempricermb, trunc(sum(shippingPricermb),2) shippingPricermb, trunc(sum(refundment),2) refundment, trunc(sum(cost),2) cost, trunc(sum(giftWrapPricermb),2) giftWrapPricermb, trunc(sum(commissionRMB),2) commissionRMB from t_order_item group by amazonorderid) t2 on t2.amazonorderid = t1.amazonorderid left join t_country t3 on t3.id = t1.buyercountry left join t_user_company t4 on t4.company_id = t1.salescompany left join (select distinct merchant_id,shop_name,create_user from t_shop) t5 on t5.merchant_id = t1.salessource left join t_user_info t6 on t6.user_id= t1.salesman left join t_transport_company t7 on t7.id = t1.transportcompany group by t1.id, t1.amazonorderid, t1.MERGEDID, t1.salesman, t1.salescompany, t1.salessource, t1.buyercountry, t1.buyeremail, t1.marketplaceId, t6.user_name, t5.shop_name, t5.merchant_id, t5.create_user, t1.runningtime, t1.intltracknum, t1.purchaseDate, t1.FULFILLMENTCHANNEL, t7.name, t1.transportCompany, t1.shippingPrice, t1.localstatus, t1.remark, t1.update_time, t3.name, t2.smallImage, t3.exrate, t4.company_name, t1.paymentmethod, t2.sku, t2.asin, t2.tracknum) p WHERE 1=1
AND p.MERGEDID = #{amazonOrderId,jdbcType=VARCHAR}
AND p.AMAZONORDERID LIKE '%${amazonOrderId}%'
AND p.SALESMAN = #{salesMan,jdbcType=VARCHAR} AND p.CREATE_USER = #{salesMan,jdbcType=VARCHAR}
AND p.SALESCOMPANY = #{salesCompany,jdbcType=VARCHAR}
AND p.SALESSOURCE = #{salesSource,jdbcType=VARCHAR}
AND p.BUYERCOUNTRY = #{buyerCounty,jdbcType=VARCHAR}
AND p.INTLTRACKNUM LIKE '%${intlTrackNum}%'
AND p.TRANSPORTCOMPANY = #{transportCompany,jdbcType=VARCHAR}
AND p.FULFILLMENTCHANNEL = 'FBA'
AND (p.LOCALSTATUS = '2' OR p.LOCALSTATUS = '11') AND p.FULFILLMENTCHANNEL != 'FBA'
AND p.LOCALSTATUS = #{localStatus,jdbcType=VARCHAR} AND p.FULFILLMENTCHANNEL != 'FBA'
AND p.UPDATE_TIME >= #{logmin,jdbcType=VARCHAR}
AND p.PURCHASEDATE >= #{logmin,jdbcType=VARCHAR}
AND p.UPDATE_TIME <= #{logmax,jdbcType=VARCHAR}
AND p.PURCHASEDATE <= #{logmax,jdbcType=VARCHAR}
AND p.SKU LIKE '%'||#{sku}||'%'
AND p.TRACKNUM LIKE '%${trackNum}%'
ORDER BY p.UPDATE_TIME DESC
ORDER BY p.LOCALSTATUS,p.PURCHASEDATE DESC
ORDER BY p.PURCHASEDATE DESC

SELECT t1.* ,trunc((t1.ITEMPRICE + t1.SHIPPINGPRICE + t1.GIFTWRAPPRICE + t1.COMMISSION),2) || '(' || t1.CURRENCYCODE || ')' ITEMPRICESUM from T_ORDER_ITEM t1 WHERE 1=1
AND t1.AMAZONORDERID IN (SELECT AMAZONORDERID FROM T_ORDER WHERE MERGEDID = #{amazonOrderId,jdbcType=VARCHAR})
AND t1.AMAZONORDERID = #{amazonOrderId,jdbcType=VARCHAR}
AND t1.ORDERITEMID = #{orderItemId,jdbcType=VARCHAR}

mapper.update:
UPDATE T_ORDER
set
UPDATE_TIME = #{updateTime,jdbcType=VARCHAR}

    ,LOCALSTATUS = #{status,jdbcType=VARCHAR}


    ,INTLTRACKNUM = #{intlTrackNum,jdbcType=VARCHAR}


    ,SHIPPINGPRICE = #{shippingPrice,jdbcType=VARCHAR}


    ,TRANSPORTCOMPANY = #{transportCompany,jdbcType=VARCHAR}

WHERE AMAZONORDERID = #{amazonOrderId,jdbcType=VARCHAR}

UPDATE T_ORDER
set
SHIPPINGPRICE = #{shippingPrice,jdbcType=VARCHAR}
WHERE AMAZONORDERID = #{amazonOrderId,jdbcType=VARCHAR}

UPDATE T_ORDER_ITEM
set
COMMISSION = #{commission,jdbcType=DOUBLE},
COMMISSIONRMB = #{commissionRMB,jdbcType=DOUBLE}
WHERE AMAZONORDERID = #{amazonOrderId,jdbcType=VARCHAR}
AND ORDERITEMID = #{orderItemId,jdbcType=VARCHAR}

UPDATE T_ORDER_ITEM
set STATUS = #{status,jdbcType=VARCHAR}

    ,COST = #{cost,jdbcType=VARCHAR}


    ,REFUNDMENT = #{refundment,jdbcType=VARCHAR}


    ,TRACKNUM = #{trackNum,jdbcType=VARCHAR}


    ,PURCHASENUM = #{purchaseNum,jdbcType=VARCHAR}

WHERE AMAZONORDERID = #{amazonOrderId,jdbcType=VARCHAR}

    AND ORDERITEMID = #{orderItemId,jdbcType=VARCHAR}

UPDATE T_ORDER
set REMARK = #{remark,jdbcType=VARCHAR}
WHERE AMAZONORDERID = #{amazonOrderId,jdbcType=VARCHAR}

mapper.select:
SELECT nvl(trunc(sum(t2.itempricermb + t2.shippingPricermb + t2.giftWrapPricermb + t2.commissionRMB),2),0) itemprice, nvl(trunc(sum(t1.shippingPrice),2),0) shippingPrice, nvl(trunc(sum(t2.cost),2),0) cost, nvl(trunc(sum(t2.refundment),2),0) refundment, nvl(trunc(sum(t2.itempricermb - t2.cost - t2.refundment - t1.shippingPrice + t2.shippingPricermb + t2.giftWrapPricermb + t2.commissionRMB),2),0) profit FROM T_ORDER t1 LEFT JOIN (select amazonorderid, wm_concat(sellersku) sku, wm_concat(tracknum) tracknum, trunc(sum(itempricermb),2) itempricermb, trunc(sum(shippingPricermb),2) shippingPricermb, trunc(sum(refundment),2) refundment, trunc(sum(cost),2) cost, trunc(sum(giftWrapPricermb),2) giftWrapPricermb, trunc(sum(commissionRMB),2) commissionRMB from t_order_item group by amazonorderid) t2 on t2.amazonorderid = t1.amazonorderid
AND t2.SKU LIKE '%'||#{sku}||'%'
AND t2.TRACKNUM LIKE '%${trackNum}%'
WHERE 1=1
AND t1.AMAZONORDERID LIKE '%${amazonOrderId}%'
AND t1.SALESMAN = #{salesMan,jdbcType=VARCHAR}
AND t1.SALESCOMPANY = #{salesCompany,jdbcType=VARCHAR}
AND t1.SALESSOURCE = #{salesSource,jdbcType=VARCHAR}
AND t1.BUYERCOUNTRY = #{buyerCounty,jdbcType=VARCHAR}
AND t1.INTLTRACKNUM LIKE '%${intlTrackNum}%'
AND t1.TRANSPORTCOMPANY = #{transportCompany,jdbcType=VARCHAR}
AND t1.FULFILLMENTCHANNEL = 'FBA'
AND (t1.LOCALSTATUS = '2' OR t1.LOCALSTATUS = '11') AND t1.FULFILLMENTCHANNEL != 'FBA'
AND t1.LOCALSTATUS = #{localStatus,jdbcType=VARCHAR} AND t1.FULFILLMENTCHANNEL != 'FBA'
AND t1.UPDATE_TIME >= #{logmin,jdbcType=VARCHAR}
AND t1.PURCHASEDATE >= #{logmin,jdbcType=VARCHAR}
AND t1.UPDATE_TIME <= #{logmax,jdbcType=VARCHAR}
AND t1.PURCHASEDATE <= #{logmax,jdbcType=VARCHAR}

mapper.update:
UPDATE T_ORDER
SET MERGEDID = #{mergedId,jdbcType=VARCHAR}
WHERE ID = #{id,jdbcType=VARCHAR}
