mapper.select:
SELECT * FROM T_USER_COMPANY
WHERE 1=1

    AND COMPANY_ID = #{id,jdbcType=VARCHAR}

mapper.update:
UPDATE T_USER_COMPANY
set
COMPANY_STATE =  #{state,jdbcType=VARCHAR}
WHERE
COMPANY_ID = #{id,jdbcType=VARCHAR}

mapper.select:
select t1.*,t2.MENU_NAME MENU_PNAME from T_MENU t1,T_MENU t2 WHERE t2.menu_id = t1.menu_pid
AND t1.MENU_ID = #{id,jdbcType=VARCHAR}
ORDER BY t1.MENU_ORDER

mapper.update:
UPDATE T_MENU
set
MENU_STATE =  #{state,jdbcType=VARCHAR}
WHERE
MENU_ID = #{id,jdbcType=VARCHAR}

mapper.select:
select t1.*, t2.role_name, t3.company_name from T_USER_INFO t1 left join T_ROLE t2 on t2.role_id = t1.role_id left join T_USER_COMPANY t3 on t3.company_id = t1.user_company WHERE 1=1
AND t1.USER_ID = #{id,jdbcType=VARCHAR}
AND t1.USER_COMPANY = #{companyId,jdbcType=VARCHAR}

mapper.update:
